import React,{useState,useRef} from 'react'

import Bg from '../assets/bg.png'
import './Home.scss'


const Home = () => {

	const [errors, setErrors] = useState([])

	const [values, setValues] = useState({
		email:'',
		password:'',
		cpassword:'',
		fullname:'',
		phone:'',
	})

	const inputs=[
		{
			id:1,
			name:"email",
			type:'email',
			label:'Email',
			required:true,
		},
		{
			id:2,
			name:"password",
			type:'password',
			label:'Password',
			errorMessage:'should be 8-20 characters and should include at least 1 lettter, 1 number and 1 special character!'
		},
		{
			id:3,
			name:"cpassword",
			type:'password',
			label:'Confirrm Password',
			errorMessage:'Passwords don\'t match!'
		},
		{
			id:4,
			name:"fullname",
			type:'text',
			label:'Fullname',
			errorMessage:'Full Name should be 12-20 characters!'
		},
		{
			id:5,
			name:"phone",
			type:'tel',
			label:'Phone Number',
			errorMessage:'Should contain only Numbers!'
		}
	]


	const ref = useRef()



	const handleSubmit=(e)=>{
		e.preventDefault()
		const data=new FormData(e.target)
	}

	const onChange=(e)=>{
		setValues({...values,[e.target.name]:e.target.value})
	}

	console.log(values)

	return (
		<div className="home">
			<div className="left_side">
				<img src={Bg} alt="bg"/>
				<div className="descript">
					<h3>Choose a date range</h3>
					<p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.</p>
				</div>
			</div>
			<div className="right_side">
				<h3>Create an Account</h3>
				<form  onSubmit={handleSubmit}>
					{
						inputs.map((item)=>(
							<div key={item.id}>
								<label htmlFor="">{item.label}</label>
								<input type={item.type} refer={ref} name={item.name} required={item.required} value={values[item.name]} onChange={onChange} />
								<span className="error">{item.errorMessage}</span>	
							</div>
							))
					}

					<div className="terms">

						<input type="checkbox"/>
						<p> I read and agree Terms and Conditions</p>	
					</div>
					<button>Create Account</button>

				</form>
			</div>
		</div>
	)
}

export default Home
